teran'  => $dateran,            'pageSize' => page_size($this->pageSize, $list->total()),            'page'     => $list->render(),            'list'     => $list,            'empty'    => empty_list(12),        ];        View::assign($view);        return View::fetch();    }    //更新    public function update(){        //获取目录        $dir = "/home/ossnenyes/yuedu/";        $file = scandir($dir);        print_r($file);die;    }}