<?php/** * Created by PhpStorm. * User: chenyouxi * Date: 2019/7/11 * Time: 5:40 PM */namespace app\common\service;use app\common\service\BaseService as BaseService;use app\common\model\ArticleCategoryModel as ArticleCategoryModel;use think\Db;class ArticleCategoryService extends BaseService{    function __construct()    {        parent::__construct();        $this->articleCategory = new ArticleCategoryModel();    }    public function getList($page_index, $page_size, $condition, $order,$type){        $list = $this->articleCategory->viewPageQuery($this->articleCategory, $page_index, $page_size, $condition, $order,$type);        return $list;    }    public function getView($condition = '', $field = '*'){        $info  = $this->articleCategory->getInfo($condition,$field);        return $info;    }}