<?php/** * Created by PhpStorm. * User: chenyouxi * Date: 2019/6/11 * Time: 10:38 AM */namespace app\common\service;use think\facade\Session as Session;use think\facade\Cookie as Cookie;use think\facade\Cache;use app\common\service\BaseService as BaseService;use app\common\model\ArticleModel as ArticleModel;use think\Db;class ArticleService extends BaseService{    function __construct()    {        parent::__construct();        $this->article = new ArticleModel();    }    public function getList($page_index, $page_size, $condition, $order,$field){        $list = $this->article->pageQuery($page_index, $page_size, $condition, $order, $field);        return $list;    }    public function getView($condition = '', $field = '*'){        $info  = $this->article->getInfo($condition,$field);        return $info;    }    public function getCount($condition){        $count  = $this->article->getCount($condition);        return $count;    }}