xt->getQuery($condition, $field, $order, $group='');        return $list;    }    //单条记录    public function getInfo($condition = '', $field = '*'){        $info = $this->imagetext->getInfo($condition, $field);        return $info;    }    public function save(){        $res = $this->imagetext->save($data = [], $where = [], $sequence = null);        return $res;    }    public function add($data,$replace = false, $getLastInsID = false, $sequence = null){        $res = $this->imagetext->add($data , $replace , $getLastInsID , $sequence );        return $res;    }}