<?php/** * Created by PhpStorm. * User: chenyouxi * Date: 2019/6/11 * Time: 11:27 AM */namespace app\common\service;use app\common\service\BaseService as BaseService;use app\common\model\IntegralModel as IntegralModel;use think\Db;class IntegralService extends BaseService{    function __construct()    {        parent::__construct();        $this->integral = new IntegralModel();    }    public function getList($page_index, $page_size, $condition, $order,$field="*"){        $list = $this->integral->pageQuery($page_index, $page_size, $condition, $order, $field);        return $list;    }    public function getSum($condition, $field){        $list = $this->integral->getSum($condition, $field);        return $list;    }}