<?php/** * Created by PhpStorm. * User: chenyouxi * Date: 2019/6/11 * Time: 3:58 PM */namespace app\common\service;use app\common\service\BaseService as BaseService;use app\common\model\TopSellerListModel;use think\Db;class TopSellerListService extends BaseService{    function __construct()    {        parent::__construct();        $this->topSellerList = new TopSellerListModel();    }    public function getList($page_index, $page_size, $condition, $order,$field="*"){        $list = $this->topSellerList->pageQuery($page_index, $page_size, $condition, $order, $field);        return $list;    }    public function count($condition){        $count = $this->topSellerList->getCount($condition);        return $count;    }    public function getQuery($condition=[], $field='*', $order='', $group=''){        $list = $this->topSellerList->getQuery($condition, $field, $order, $group='');        return $list;    }    public function getInfo($condition = '', $field = '*'){        $info = $this->topSellerList->getInfo($condition  , $field );        return $info;    }}