<!DOCTYPE html><html><head>    <meta charset="utf-8">    <!--渲染器-->    <meta name="renderer" content="webkit">    <!--优先使用最新版本的IE 和 Chrome 内核-->    <meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">    <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1">    <title>        91AMZ-微信后台管理    </title>    <link rel="stylesheet" href="/public/static/layui/css/layui.css">    <link rel="stylesheet" href="/public/static/admin/css/style.css">    <script src="/public/static/layui/layui.js">    </script>    <script src="/public/static/layui/js/jquery.min.js">    </script>    <script>        layui.use('layer',            function () {                var $ = layui.jquery, layer = layui.layer;            })    </script></head><body><div id="video_wrapper">    <video autoplay muted loop>        <source src="/public/static/admin/coverr/Nature-Sunset.mp4" type="video/mp4">    </video></div><style>    #video_wrapper {        margin: 0px;        padding: 0px;    }    #video_wrapper video {        position: fixed;        top: 50%;        left: 50%;        z-index: -100;        min-width: 100%;        min-height: 100%;        width: auto;        height: auto;        transform: translate(-50%, -50%);    }</style><div class="layadmin-user-login layadmin-user-display-show" id="LAY-user-login">    <div class="layadmin-user-login-main">        <div class="layadmin-user-login-box layadmin-user-login-header">            <h2>                91amz            </h2>        </div>        <div class="layadmin-user-login-box layadmin-user-login-body layui-form">            <div class="layui-form-item">                <label class="layadmin-user-login-icon layui-icon layui-icon-username"                       for="LAY-user-login-username">                </label>                <input type="text" name="username" id="LAY-user-login-username" lay-verify="required"                       placeholder="用户名" class="layui-input">            </div>            <div class="layui-form-item">                <label class="layadmin-user-login-icon layui-icon layui-icon-password"                       for="LAY-user-login-password">                </label>                <input type="password" name="password" id="LAY-user-login-password" lay-verify="required"                       placeholder="密码" class="layui-input">            </div>            <div class="layui-form-item">                <div class="layui-row">                    <div class="layui-col-xs5">                        <label class="layadmin-user-login-icon layui-icon layui-icon-vercode"                               for="LAY-user-login-vercode">                        </label>                        <input type="text" name="vercode" id="LAY-user-login-vercode" lay-verify="required"                               placeholder="验证码" class="layui-input">                    </div>                    <div class="layui-col-xs7">                        <div style="margin-left: 10px;">                            <img src="/admin/captcha" class="layadmin-user-login-codeimg"  onclick="this.src='/website/verify?tag=1'+'&amp;send='+Math.random()" alt="看不清; 点击更换校验码" title="看不清; 点击更换校验码">                        </div>                    </div>                </div>            </div>            <div class="layui-form-item">                <button class="layui-btn layui-btn-fluid login" lay-submit lay-filter="LAY-user-login-submit">                    登 录                </button>            </div>            <div class="layui-trans layadmin-user-login-footer">                <p>                    91AMZ ©                </p>                <p>                    <span>                        <a href="httpx://www.91amz.com/" target="_blank">                            前往官网                        </a>                    </span>                </p>            </div>        </div>    </div></div><script>    $(document).keyup(function (event) {        if (event.keyCode == 13) {            $(".login").trigger("click");        }    });    $(function () {        //刷新验证码操作        //后台登录        $(".login").click(function () {            var username = $("input[name='username']").val();            var password = $("input[name='password']").val();            var vercode = $("input[name='vercode']").val();            $.ajax({                type: "post",                url: "/admin/checkLogin",                data: {                    username: username,                    password: password,                    vercode: vercode                },                dataType: "json",                success: function (data) {                    console.log(data);                    if(data.status == 'success'){                        layer.load();                        window.location.href = "/admin/index";                    } else {                        layer.alert(data.msg, {                            icon: 2                        });                    }                },                error: function (xhr) {                }            });        })    })</script></body></html>